mapper.insert:


    	select NVL(MAX(employee_id), 100) + 1
		from employees

    INSERT INTO employees
    (
    	employee_id
    	,last_name
    	,email
    	,job_id
    	,hire_date

    	,salary

    	)
    VALUES
    (

    	#{employeeId}
    	,#{lastName}
    	,#{email}
    	,#{jobId}


    	,#{hireDate}


    	,sysdate



    	,#{salary}	

    )

mapper.update:
UPDATE employees
SET
	last_name = #{lastName}


	, job_id = #{jobId}


	, hire_date = #{hireDate}

where employee_id = #{employeeId}
